import React from 'react';

// import styles from MUI
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
imopprt 

// import icons
import CalendarViewDayIcon from '@material-ui/icons/CalendarViewDay';
import FitnessCenterIcon from '@material-ui/icons/FitnessCenter';
import StraightenIcon from '@material-ui/icons/Straighten';

// import files
import styles from '../../assets/jss/components/list/dashlistStyle.js';

const useStyles = makeStyles(styles);

export default function DashActivityList() {
    const classes = useStyles();

    const activity = [
        {
            name: "All activities",
            icon: CalendarViewDayIcon,
        },
        {
            name: "Gym activity",
            icon: FitnessCenterIcon,
        },
        {
            name: "Piano activity",
            icon: StraightenIcon,
        }
    ]

    const [selectedIndex, setSelectedIndex] = React.useState(0);

    // handle onclick 
    const handleListItemClick = (event, index) => {
        setSelectedIndex(index);
    };

    var calendarList = (
        <List className={classes.list}>
            {activity.map((prop, index) => {
                return (
                    // create the href for the list item
                    <ListItem
                        button
                        selected={selectedIndex === index}
                        className={
                            classNames(classes.drawerButton)
                        }
                        key={prop.name}
                        onClick={(event) => handleListItemClick(event, index)}
                    >
                        <prop.icon
                            className={classes.buttonIcon}
                            fontSize="large"
                        />
                        <ListItemText
                            disableTypography
                            className={classes.buttonText}
                            primary={prop.name}
                        />
                    </ListItem>
                );
            })}
        </List>
    );
    return (
        <Paper>
            <Typography className={classes.listTitle} variant="h6">Activity</Typography>
            <Paper elevation={3}>
                {calendarList}
            </Paper>
        </Paper>
    );
}
