import React, { useRef, useLayoutEffect } from 'react';

// import recharts
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// import material ui core 
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import moment from 'moment';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';

import styles from '../../assets/styles/views/gym/gymgraphStyle.js';

const useStyles = makeStyles(styles);

export default function DashGraph(props) {
    const classes = useStyles();
    const targetRef = useRef();

    // states
    const [state, setState] = React.useState({
        width: 0,
        height: 0,
        popover: false
    });
    const handlePopover = () => {
        setState({ ...state, popover: true });
    };
    const handlePopoverClose = () => {
        setState({ ...state, popover: false });
    }
    useLayoutEffect(() => {
        if (targetRef.current) {
            setState({
                ...state,
                width: targetRef.current.offsetWidth,
                height: targetRef.current.offsetHeight
            });
        }
    }, []);

    const titleCase = (str) => {
        var splitStr = str.toLowerCase().split(' ');
        for (var i = 0; i < splitStr.length; i++) {
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1);
        }
        return splitStr.join(' ');
    }

    // function that selects data based on the selected workout and date range 
    const filteredData = props.data.map(workout => {
        return {
            name: workout.name,
            workoutdata: [
                workout.data.map(data => {
                    if (moment(data.date).isBetween(props.start, props.end)) {
                        return {
                            data
                        };
                    }
                })
            ]
        };
    })


    const getData = () => {
        if (filteredData.length === 0) {
            //handlePopover()
            return ([
                {
                    "date": "2020-01-11",
                    "weight": 0
                },
                {
                    "date": "2020-01-12",
                    "weight": 0
                },
                {
                    "date": "2020-01-13",
                    "weight": 0
                },
            ]);        
        } else {
            let tableData = []
            props.data.map((arr) => {
                tableData.push(arr.data)
            })
            return (tableData);
        }
    }

    const getLabel = () => {
        if (props.data.length === 0) {
            return ("No data");
        } else {
            props.data.map((arr) => {
                return (
                    arr.name
                );
            })
        }
    }
    // style

    return (
        <Paper className={classes.paper} ref={targetRef} >
            <ResponsiveContainer width={state.width} height={state.height} className={classes.container}>
                <LineChart data={getData()}
                    margin={{ top: 30, right: 50, left: 20, bottom: 0 }}>
                    <XAxis dataKey="date" />
                    <YAxis dataKey="weight" domain={['auto', 'auto']} />
                    <CartesianGrid strokeDasharray="3 3" />
                    <Tooltip />
                    <Legend height={36} wrapperStyle={{ top: 0, left: 25 }} />
                    <Line name={getLabel()} type="monotone" dataKey="weight" />
                </LineChart>
            </ResponsiveContainer>
            <Popover
                open={state.popover}
                onClose={handlePopoverClose}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                }}
            >
                <Typography className={classes.typography}>The content of the Popover.</Typography>
            </Popover>
        </Paper>
    );
}
