    const theme = useTheme(styles);
    const targetRef = React.useRef();
    const targetRef1 = React.useRef();
    // styles 
    const classes = useStyle();
    const matches = useMediaQuery(theme.breakpoints.up('sm'));
    // states 
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    const [state, setState] = React.useState(
        {
            currentRow: {},
            selected: true,
            selectedRowId: 0,
            pianoCurrentRow: {},
            pianoSelected: false,
            pianoSelectedRowId: null,
            cardIndex: null,
            currMusicIndex: 0,
            width: 0,
            sheetHeight: 0,
            buttonLocation: 0,
            playing: false,
            pageWidth: 0,
            musicColumns: [
                { title: 'Group', field: 'group' },
                { title: 'Name', field: 'name' },
                { title: 'Composer', field: 'composer' },
            ],
            sheetIndex: 0,
        }
    );
    // media player states 
    const handleChangeMusic = (control) => {
        if (control === "prev" && state.currMusicIndex != 0) {
            setState({ ...state, currMusicIndex: state.currMusicIndex - 1 })
        } else if (control === "next" && state.currMusicIndex != musicData.length) {
            setState({ ...state, currMusicIndex: state.currMusicIndex + 1 })
        }
    }
    const handlePlayPause = () => {
        setState({ ...state, playing: !state.playing })
        setExpanded(false);
    }
    const handleEnded = () => {
        setState({ ...state, playing: false })
    }
    // const handleChange = (panel, index) => (event, isExpanded) => {
    //     setExpanded(isExpanded ? panel : false);
    //     props.handleIndexChange(index);
    // }
    const handleCardClick = (index) => {
        if (index === state.cardIndex) {
            setState({ ...state, cardIndex: null })
        } else {
            setState({ ...state, cardIndex: index })
        }
    }
    // pdf states 
    const [numPages, setNumPages] = React.useState(null);
    const [pageNumber, setPageNumber] = React.useState(1);
    function onDocumentLoadSuccess({ numPages }) {
        setNumPages(numPages);
        setPageNumber(1);
    }
    function changePage(offset) {
        setPageNumber(prevPageNumber => prevPageNumber + offset);
    }
    function previousPage() {
        changePage(-1);
    }
    function nextPage() {
        changePage(1);
    }

