                        <Grid item xs={7}>
                            <CardContent className={classes.content}>
                                <Typography gutterBottom variant="h5" component="h2"> {musicData[state.currMusicIndex].name} </Typography>
                                <Typography variant="subtitle1" color="textSecondary"> {musicData[state.currMusicIndex].subtitle}</Typography>
                            </CardContent>
                            <div className={classes.controls}>
                                <ReactPlayer
                                    width="0"
                                    height="0"
                                    playing={state.playing}
                                    url={musicData[state.currMusicIndex].fullUrl}
                                    onEnded={() => handleEnded()}
                                />
                                <IconButton aria-label="previous" onClick={() => handleChangeMusic("prev")}>
                                    {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
                                </IconButton>
                                <IconButton aria-label="play/pause" onClick={() => handlePlayPause()}>
                                    {state.playing ? <PauseIcon className={classes.playIcon} /> :
                                        <PlayArrowIcon className={classes.playIcon} />}
                                </IconButton>
                                <IconButton aria-label="next" onClick={() => handleChangeMusic("next")}>
                                    {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
                                </IconButton>
                            </div>
                        </Grid>
                        <Grid item xs={5} style={{ marginTop: "auto" }}>
                            <img
                                style={{ maxHeight: state.width }}
                                src={`https://img.youtube.com/vi/${musicData[state.currMusicIndex].src}/0.jpg`}
                            />
                        </Grid>
                    </Grid>
                    <IconButton
                        className={clsx(classes.expand, {
                            [classes.expandOpen]: expanded,
                        })}
                        style={{ position: "relative", left: state.buttonLocation, top: "-55px" }}
                        onClick={handleExpandClick}
                        aria-expanded={expanded}
                        aria-label="show more"
                    >
                        <ExpandMoreIcon />
                    </IconButton>
                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                        <CardContent>
                            {displayMusicLibrary()}
                        </CardContent>
                    </Collapse>
                </Card>
            </Grid>
            <Grid item xs={6} >
                <Card style={{ maxHeight: expanded ? "" : state.width }}>
                    <CardContent>
                        <div className={classes.cardColumn}>
                            <FileCopyIcon size="large" style={{ marginRight: "32px", marginTop: "4px" }} />
                            <Typography gutterBottom variant="h5" component="h2"> Piano Sheet Library </Typography>
                        </div>
                    </CardContent>
                    <CardActions>
                        <IconButton >
                            <ChevronLeftIcon />
                        </IconButton>
                        <Typography variant="body2" color="textSecondary" component="p"> {titleCase(sheetData[state.sheetIndex].name)}</Typography>
                        <Typography variant="body2" color="textSecondary" component="p"> {titleCase(sheetData[state.sheetIndex].composer)}</Typography>
                        <IconButton style={{ paddingLeft: "0px" }}>
                            <ChevronRightIcon />
                        </IconButton>
                    </CardActions>
                </Card>
            </Grid>
            <Typography className={classes.typo} variant="h5" style={{ display: expanded ? "none" : "" }}>Gym</Typography>
            <Grid item xs={[2].includes(state.cardIndex) ? 8 : 4} style={{ display: expanded ? "none" : "" }}>
                <Card>
                    <CardActionArea onClick={() => handleCardClick(2)}>
                        <CardContent>
                            <div className={classes.cardColumn}>
                                <AddToPhotosIcon size="large" style={{ marginRight: "32px", marginTop: "4px" }} />
                                <Typography gutterBottom variant="h5" component="h2"> Add a Gym Entry </Typography>
                            </div>
                        </CardContent>
                    </CardActionArea>
                </Card>
            </Grid>
            <Grid item xs={[3].includes(state.cardIndex) ? 8 : 4} style={{ display: expanded ? "none" : "" }}>
                <Card>
                    <CardActionArea onClick={() => handleCardClick(3)}>
                        <CardContent>
                            <div className={classes.cardColumn}>
                                <InsertInvitationIcon size="large" style={{ marginRight: "32px", marginTop: "4px" }} />
                                <Typography gutterBottom variant="h5" component="h2"> Next Workout </Typography>
                            </div>
                            <Typography variant="body2" color="textSecondary" component="p"
                                style={{ paddingTop: "16px" }} className={classes.subTypo} >
                                Next workout day is : {titleCase(workoutRoutine[0].routineName)}
                            </Typography>
                            {workoutRoutine[0].workouts.map((routine, index) => {
                                return (
                                    <Typography key={routine.workout.name + index} variant="body2" color="textSecondary" component="p" className={classes.subTypo}>
                                        {(index + 1)} : {titleCase(routine.workout.name)}
                                    </Typography>
                                );
                            })}
                        </CardContent>
                    </CardActionArea>
                </Card>
            </Grid>
        </Grid>

        // <div className={classes.container}>
