const gyminputformStyle = theme => ({
    paper: {
        display: "flex",
        width: "inherit",
        flexDirection: "column",
        justifyContent: "flex-start",
        [theme.breakpoints.up("sm")]: {
            marginLeft: "1rem",
            minHeight: "100%"
        }
    },
    select: {
        display: "flex",
        justifyContent: "space-around",
        flexDirection: "row",
        width: "100%",
        marginTop: "1.5rem",
        marginLeft: "1rem"
    },
    datepicker: {
        fontSize: "1rem",
    }
});

export default gyminputformStyle;
