import React from 'react';

// import material-ui/cores from MUI 
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import { Hidden, Drawer } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton'

// import styles from MUI
import { makeStyles, useTheme } from '@material-ui/core/styles';
import LoopIcon from '@material-ui/icons/LoopIcon';
import SkipPreviousIcon from '@material-ui/icons/SkipPreviousIcon';
import SkipNextIcon from '@material-ui/icons/SkipNextIcon';
import PlayArrowIcon from '@material-ui/icons/PlayArrowIcon';
import PauseIcon from '@material-ui/icons/PauseIcon';

// import files 
import styles from '../assets/styles/components/sidebarStyle.js';
import drawerItems from './list/DrawerList.js';
import Appbar from '../components/Appbar.js';


const useStyles = makeStyles(styles);

export default function SideBar(props) {
    const styleProps = {
        color: props.theme.colors.primary
    }
    const classes = useStyles(styleProps);
    const theme = useTheme(styles);

    cosnt [state, setState] = React.useState();
    const [mobileOpen, setMobileOpen] = React.useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    }
    const handleLoop = () => {
        setState({ ...state, snackbarOpen: true });
        props.handleLoop();
    }
    const mediaPlayer = () => {
        return (
            <div className={classes.controls}>
                <IconButton aria-label="previous" onClick={() => props.handleChangeMusic("prev")}>
                    {theme.direction === 'rtl' ? <SkipNextIcon /> : <SkipPreviousIcon />}
                </IconButton>
                <IconButton aria-label="play/pause" onClick={() => props.handlePlayPause()}>
                    {props.playing ? <PauseIcon className={classes.playIcon} /> : <PlayArrowIcon className={classes.playIcon} />}
                </IconButton>
                <IconButton aria-label="next" onClick={() => props.handleChangeMusic("next")}>
                    {theme.direction === 'rtl' ? <SkipPreviousIcon /> : <SkipNextIcon />}
                </IconButton>
                <IconButton style={{ marginLeft: "16px" }} onClick={() => handleLoop()}>
                    <LoopIcon style={{ color: props.loop ? props.theme.colors.primary : "" }} />
                </IconButton>
                <Snackbar
                    open={state.snackbarOpen} autoHideDuration={5000} onClose={() => setState({ ...state, snackbarOpen: false })}
                    message={props.loop ? "Loop Enabled" : "Loop Disabled"}
                />
            </div>
        );
    }

    return (
        <div className={classes.wrapper}>
            <Appbar
                routes={props.routes}
                theme={props.theme}
                handleDrawerToggle={() => handleDrawerToggle()}
            />
            <Hidden xsDown implementation="css">
                <Drawer
                    classes={{ paper: classes.drawerPaper }}
                    variant="permanent"
                    open
                    ModalProps={{
                        keepMounted: true, // Better open performance on mobile.
                    }}
                >
                    <Typography
                        className={classes.sidebarTitle}
                        variant="h5"
                    >
                        Record Keeper
                    </Typography>
                    {drawerItems(props)}

                </Drawer>
            </Hidden>
            <Hidden smUp implementation="css">
                <Drawer
                    classes={{ paper: classes.drawerPaper }}
                    variant="temporary"
                    open={mobileOpen}
                    onClose={handleDrawerToggle}
                    ModalProps={{
                        keepMounted: true, // Better open performance on mobile.
                    }}
                >
                    <Typography
                        className={classes.sidebarTitle}
                        variant="h6"
                    >
                        Record Keeper
                    </Typography>
                    {drawerItems(props)}
                    {console.log(window.location.href)}
                </Drawer>
            </Hidden>
        </div>
    );
}
