const gyminputformStyle = theme => ({
    paper: {
        display: "flex",
        flexWrap: "wrap",
        width: "inherit",
        flexDirection: "row",
        justifyContent: "flex-start",
        [theme.breakpoints.up("sm")]: {
            marginLeft: "1rem",
            minHeight: "100%"
        }
    },
    select: {
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "space-around",
        flexDirection: "row",
        width: "inherit"
    },
    datepicker: {
        fontSize: "1rem",
        width: "30%"
    }
});

export default gyminputformStyle;
