import React, { useRef, useLayoutEffect } from 'react';

// import recharts
import {
    ScatterChart, XAxis, YAxis, Scatter, Tooltip, Legend,
} from 'recharts';

// import material ui core 
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import moment from 'moment';

import styles from '../../assets/styles/views/gym/gymgraphStyle.js';

const useStyles = makeStyles(styles);

export default function DashGraph(props) {
    const classes = useStyles();
    const targetRef = useRef();

    // states
    const [state, setState] = React.useState({
        width: 0,
        height: 0,
    });

    useLayoutEffect(() => {
        if (targetRef.current) {
            setState({
                ...state,
                width: targetRef.current.offsetWidth,
                height: targetRef.current.offsetHeight
            });
        }
    }, []);

    const titleCase = (str) => {
        var splitStr = str.toLowerCase().split(' ');
        for (var i = 0; i < splitStr.length; i++) {
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1);
        }
        return splitStr.join(' ');
    }

    // function that selects data based on the selected workout and date range 
    const filteredByDate = props.data.map(workout => {
        return {
            name: workout.name,
            workoutdata: [
                workout.data.map(data => {
                    if (moment(data.date).isBetween(props.start, props.end)) {
                        return {
                            data
                        };
                    }
                })
            ]
        };
    })

    const getData = () => {
        if (filteredByDate.length === 0) {
            return ([
                {
                    "time": moment().subtract(2, 'days').valueOf(),
                    "value": 0
                },
                {
                    "time": moment().subtract(1, 'days').valueOf(),
                    "value": 0
                },
                {
                    "time": moment().valueOf(),
                    "value": 0
                },
            ]);
        } else {
            let chartData = []
            filteredByDate.map(data => {
                chartData.push(data.workoutdata[0])
            })
            console.log(chartData[0])
            return chartData
        }
    }

    const getLabel = () => {
        if (props.data.length === 0) {
            return (
                <Scatter
                    data={getData}
                    line={{ stroke: '#008000' }}
                    lineJointType='monotoneX'
                    lineType='joint'
                    name='Values'
                />
            );
        } else {
            props.data.map((arr) => {
                return (
                    arr.name
                );
            })
        }
    }
    // style

    return (
        <Paper className={classes.paper} ref={targetRef} >
            <ScatterChart data={getData()}
                className={classes.chart}
                width={state.width} height={state.height}
                margin={{ top: 30, right: 50, left: 20, bottom: 0 }}>
                <XAxis dataKey='time'
                    domain={['auto', 'auto']}
                    name='Time'
                    tickFormatter={(unixTime) => moment(unixTime).format('MM-DD-YYYY')}
                    type='number'
                />
                <YAxis dataKey='value' name='Value' />
                <Scatter
                    data={getData}
                    line={{ stroke: '#008000' }}
                    lineJointType='monotoneX'
                    lineType='joint'
                    name='Values'
                />
                <Tooltip />
                <Legend height={36} wrapperStyle={{ top: 30, left: 25 }} />
            </ScatterChart>
        </Paper>


    );
}
