import React from 'react';
import {
    titleCase
} from '../../assets/styles/masterStyle.js';

// import recharts
import {
    ScatterChart, XAxis, YAxis, Scatter, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// import material ui core 
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import moment from 'moment';

// import material ui icons 
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';

import styles from '../../assets/styles/views/gym/gymgraphStyle.js';
import workoutData from '../../assets/data/gymData.json'
import GymSelection from './GymSelection.js';

const useStyles = makeStyles(styles);

export default function DashGraph(props) {
    const classes = useStyles();

    //states
    const [state, setState] = React.useState(
        {
            selectedData: [],
            selectedStartDate: moment().subtract(29, 'days'),
            selectedEndDate: moment(),
            selectedStartWeek: moment().subtract(14, 'days'),
            selectedEndWeek: moment(),
        }
    )
    const handleWeekChange = (direction) => {
        if (!direction === "right" && state.selectedEndWeek === moment()) {
            setState({ ...state, selectedEndWeek: state.selectedEndWeek.add(14, 'days') });
            setState({ ...state, selectedStartWeek: state.selectedStartWeek.add(14, 'days') });
        } else if (direction === 'left') {
            setState({ ...state, selectedEndWeek: state.selectedEndWeek.subtract(14, 'days') });
            setState({ ...state, selectedStartWeek: state.selectedStartWeek.subtract(14, 'days') });
        }
    }
    const handleDataSelection = (data) => {
        setState({ ...state, selectedData: data })
    };
    const handleDateChange = (start, end) => {
        setState({ ...state, selectedStartDate: start, selectedEndDate: end })
    };

    // function that selects data based on the selected workout and date range 
    const filteredByDate = state.selectedData.map(workout => {
        return {
            name: workout.name,
            workoutdata:
                workout.data.map(data => {
                    if (moment(data.date).isBetween(state.selectedStartDate, state.selectedEndDate)) {
                        return {
                            data
                        };
                    }
                })
        };
    })

    const getScatterData = () => {
        if (filteredByDate.length === 0) {
            return (
                <Scatter
                    data={[]}
                    line={{ stroke: props.theme.colors.primary }}
                    lineJointType='monotoneX'
                    lineType='joint'
                    legendType="line"
                />
            );
        } else {
            return (
                filteredByDate.map((prop, key) => {
                    if (!prop.workoutdata.includes(undefined)) {
                        return (
                            <Scatter
                                key={key}
                                data={
                                    prop.workoutdata.map(workout => {
                                        return {
                                            "time": moment(workout.data.date).valueOf(),
                                            "value": workout.data.weight
                                        }
                                    })
                                }
                                line={{ stroke: props.theme.colors.primary }}
                                lineJointType='monotoneX'
                                lineType='joint'
                                legendType="circle"
                                name={prop.name}
                            />
                        );
                    } else {
                        return (
                            <Scatter
                                data={[]}
                                line={{ stroke: props.theme.colors.primary }}
                                lineJointType='monotoneX'
                                lineType='joint'
                                legendType="line"
                                name={"Not Enough Data for " + prop.name}
                            />
                        );
                    }
                })
            );
        }
    }
    // style
    const options = workoutData.map((option) => {
        return {
            name: titleCase(option.workout.name), group: titleCase(option.workout.musclegroup),
            ...option
        };
    });

    return (
        <div style={{ display: "flex", flexDirection: "column", height: "76vh" }}>
            <div style={{ display: "flex", marginTop: "24px", marginLeft: "auto", marginRight: "auto" }}>
                <IconButton style={{ marginLeft: "16px" }} onClick={() => handleWeekChange('left')}>
                    <ChevronLeftIcon />
                </IconButton>
                <div style={{ marginRight: "5vw", marginLeft: "5vw" }}>
                    <Typography variant="h5" component="h1" style={{ textAlign: "center" }}>
                        {state.selectedStartWeek.format('MMMM D')} - {state.selectedEndWeek.format('MMMM D')}
                    </Typography>
                    <Typography style={{ textAlign: "center", padding: "8px" }} variant="subtitle1" color="textSecondary">
                        Total Entries
                    </Typography>
                </div>
                <IconButton style={{ marginRight: "16px" }} onClick={() => handleWeekChange('right')}>
                    <ChevronRightIcon />
                </IconButton>
            </div>
            <ResponsiveContainer minHeight={100} width="100%" height="100%">
                <ScatterChart
                    className={classes.chart}
                    margin={{ top: 0, right: 24, left: 12, bottom: 30 }}>
                    <XAxis dataKey='time'
                        domain={['auto', 'auto']}
                        name='Time'
                        tickFormatter={(unixTime) => moment(unixTime).format('MM-DD-YYYY')}
                        type='number'
                    />
                    <YAxis dataKey='value' name='Weight' domain={['auto', 'auto']} unit="lbs" />
                    {getScatterData()}
                    <Tooltip />
                    <Legend height={36} wrapperStyle={{ top: 30, left: 25 }} />
                </ScatterChart>
            </ResponsiveContainer>
        </div>
    );
    {/* <GymSelection
                handleDataSelection={(data) => handleDataSelection(data)}
                theme={props.theme}
                start={state.selectedStartDate}
                end={state.selectedEndDate}
                handleDateChange={(start, end) => handleDateChange(start, end)}
            /> */}
}
