    }

    return (
        <div className={classes.container}>
            <Grid
                container


                justify="space-evenly"
                alignItems="center"
                className={classes.grid}
                spacing={4}
            >
                {activities.map((prop, index) => {
                    return (
                        <Zoom
                            in={checked[index]}
                            key={index}
                        >
                            <Grid
                                item
                                xs={6} sm={3}
                            >
                                <Paper
                                    elevation={3}
                                    className={
                                        classes[prop.id + "Background"]
                                    }
                                >
                                    <Typography className={classes.statTitle} variant="h5">{prop.name}</Typography>
                                    <Typography className={classes.statTitle} variant="h5">{dashFunc.getAllActivity()[index].length}</Typography>
                                    <Typography className={classes.statTitle} variant="h6">Days</Typography>
                                </Paper>
                            </Grid>
                        </Zoom>
                    );
                })}
                <Grid item xs={10} sm={10}>
                    <Paper className={classes.paperCalendar} elevation={3}>
                        <Typography className={classes.calTitle} variant="h5">Daily Activity</Typography>
                        <ThemeProvider>
                            <SvgWrapper
                                height={70}
                                width={400}
                                margin={{ left: 0, right: 0, top: 10, bottom: 0 }}
                            >
                                <BoxLegendSvg
                                    anchor="center"
                                    data={activities[calendarIndex].legends}
                                    containerWidth={400}
                                    containerHeight={70}
                                    height={100}
                                    width={400}
                                    direction="row"
                                    itemWidth={90}
                                    itemHeight={20}
                                    itemsSpacing={15}
                                    padding={10}
                                    symbolSize={20}
                                    symbolShape="square"
                                    fontSize="30px"
                                />
                            </SvgWrapper>
                        </ThemeProvider>
                        <Calendar
                            margin={{ top: 40, right: 40, bottom: 40, left: 40 }}
                            colors={activities[calendarIndex].color}
                            align="top"
                            translateY={-40}
                            itemCount={0}
                            data={dashFunc.getAllActivity()[calendarIndex]}
                        />
                    </Paper>
                </Grid>
                <Grid item xs={2} sm={2}>
                    <Paper elevation={3}>
                        <DashList
                            handleChange={(index) => handleItemClicked(index)}
                        />
                    </Paper>
                </Grid>
            </Grid>
        </div >
    );
}
