const themeStyle = theme => ({
    container: {
        display: "flex",
        flexWrap: "wrap",
        flexDirection: "row",
        justifyContent: "center" 
    },
    button: {
        margin: "2rem",
        width: "30%",
        height: "0",
        paddingTop: "30%"
    },
    select: {
        flexGrow: "4",
        marginTop: "2rem",
        marginLeft: "3rem",
        marginRight: "3rem",
        height: "2rem",
        width: "100%",
        textAlign: "center"
    },
    text: {
        width: "30%",
        height: "40%"
    }
})

export default themeStyle;
