        justifyContent: "flex-start",
        width: "100%"
    },
    toggleText: {
        display: "flex",
        flexWrap: "wrap",
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center"
    },
    accordion: {
        width: "360px",
        borderRadius: "5px",
        marginTop: theme.spacing(3),
    },
    accordionSummary: {
        padding: "0 0px !important",
        height: "54px",
        [theme.breakpoints.up("sm")]: {
            height: "52px",
        }
    },
    typo: {
        fontSize: "1rem",
        [theme.breakpoints.up("sm")]: {
            paddingLeft: theme.spacing(5),
            fontSize: "2rem"
        },
        width: "inherit",
        paddingTop: theme.spacing(6),
    },
    switchTypo: {
        fontSize: "0.8rem",
        [theme.breakpoints.up("sm")]: {
            fontSize: "1.5rem"
        },
    },
    column: {
        paddingLeft: "16px",
        display: "flex",
        [theme.breakpoints.up("sm")]: {
            width: "100%",
            paddingTop: "22px",
            height: "72px"
        }
    },
    details: {
        display: 'flex',
        flexDirection: 'column',
    },
    content: {
        flex: '1 0 auto',
    },
    cover: {
        width: 151,
    },
    controls: {
        display: 'flex',
        alignItems: 'center',
        paddingLeft: theme.spacing(1),
        paddingBottom: theme.spacing(1),
    },
    playIcon: {
        height: 38,
        width: 38,
    },
    cardColumn: {
        display: "flex",
        paddingLeft: "16px",
        [theme.breakpoints.up("sm")]: {
            width: "100%",
            paddingTop: "12px",
            height: "72px"
        }
    },
    heading: {
        fontSize: "0.8rem",
        [theme.breakpoints.up("sm")]: {
            fontSize: "1.2rem"
        }
    },
    secondaryHeading: {
        fontSize: "0.8rem",
        marginLeft: "1rem",
        color: theme.palette.text.secondary,
        [theme.breakpoints.up("sm")]: {
            fontSize: "1.2rem",
            marginLeft: "1rem",
        }
    },
    icon: {
        marginRight: "16px",
        [theme.breakpoints.up("sm")]: {
            marginRight: "24px",
        }
    },
    tableitem: {
        textTransform: "capitalize",
        fontSize: "1rem"
    },
})

export default dashcontainerStyle;
