import React from 'react'
import { useRef, useLayoutEffect } from 'react'
import PropTypes from 'prop-types';
import moment from 'moment';

import Paper from '@material-ui/core/Paper';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import MaterialTable from 'material-table';

import useMediaQuery from '@material-ui/core/useMediaQuery';

import styles from '../../assets/styles/views/gym/gymcontainerStyle.js';

import { icons } from '../../assets/styles/masterStyle.js';
import GymGraph from './GymGraph.js';
import routineJson from '../../assets/data/workoutRoutine.json';

// data
import workoutData from '../../assets/data/gymData.json';
import workoutJson from '../../assets/data/workouts.json';

import GymSelection from './GymSelection.js';

const useStyles = makeStyles(styles);

function TabPanel(props) {
    const { children, value, index, ...other } = props;
    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`scrollable-auto-tabpanel-${index}`}
            aria-labelledby={`scrollable-auto-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box style={{ height: "100%" }}>
                    {children}
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `scrollable-auto-tab-${index}`,
        'aria-controls': `scrollable-auto-tabpanel-${index}`,
    };
}

// Functions 
const titleCase = (str) => {
    let splitStr = str.toLowerCase().split(' ');
    for (var i = 0; i < splitStr.length; i++) {
        splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1);
    }
    return splitStr.join(' ');
};

export default function GymContainer(props) {
    const classes = useStyles();
    // states 
    const targetRef = useRef();
    //states 
    const [state, setState] = React.useState({
        selectedData: [],
        selectedStartDate: moment().subtract(29, 'days'),
        selectedEndDate: moment(),
        width: 0, height: 0
    })

    // react state for tabs 
    const handleDataSelection = (data) => {
        setState({ ...state, selectedData: data })
    };
    const handleDateChange = (start, end) => {
        setState({ ...state, selectedStartDate: start, selectedEndDate: end })
    };

    useLayoutEffect(() => {
        if (targetRef.current) {
            setState({
                ...state,
                width: targetRef.current.offsetWidth,
                height: targetRef.current.offsetHeight
            });
        }
    }, [])

    const createTabPanel = () => {
        return (
            <Paper elevation={0} className={classes.paperHidden} >
                <AppBar position="static" color="default">
                    <Tabs
                        value={props.tabIndex}
                        onChange={handleChange}
                        indicatorColor="primary"
                        textColor="primary"
                        variant="scrollable"
                        scrollButtons="auto"
                    >
                        <Tab className={classes.tab} label="Workout Statistics" {...a11yProps(0)} />
                        <Tab className={classes.tab} label="One Time Rep Statistics" {...a11yProps(1)} />
                        <Tab className={classes.tab} label="Manage Workouts" {...a11yProps(2)} />
                        <Tab className={classes.tab} label="Manage Routines" {...a11yProps(3)} />
                    </Tabs>
                </AppBar>
                <TabPanel className={classes.tabpanel} value={props.tabIndex} index={0} >
                    <GymGraph
                        data={props.selectedData}
                        start={props.start}
                        end={props.end}
                        theme={props.theme}
                        width={props.width}
                        height={props.height}
                    />
                </TabPanel>
                <TabPanel value={props.tabIndex} index={1} >

                </TabPanel>
                <TabPanel value={props.tabIndex} index={2}>

                </TabPanel>
                <TabPanel value={props.tabIndex} index={3}>

                </TabPanel>
            </Paper>
        );
    }
    return (
        <Grid
            container
            className={classes.container}
            spacing={6}
        >
            <Grid ref={targetRef} className={classes.gymContainer} item xs={(props.tabIndex === 0) ? 8 : 12} >
                {createTabPanel()}
            </Grid>
            <Grid className={classes.gymSelection} item xs={4}>
                <GymSelection
                    handleDataSelection={(data) => handleDataSelection(data)}
                    theme={props.theme}
                    start={state.selectedStartDate}
                    end={state.selectedEndDate}
                    handleDateChange={(start, end) => handleDateChange(start, end)}
                    tabIndex={props.tabIndex}
                />
            </Grid>
        </Grid>
    );
}
