import React from 'react';
import { Switch, Route } from "react-router-dom";

// import styles
import { makeStyles } from '@material-ui/core/styles';
import { ThemeProvider as MuiThemeProvider } from '@material-ui/core/styles';

// import core ui 
import CssBaseline from '@material-ui/core/CssBaseline';

// import files 
import Sidebar from '../components/Sidebar.js';
import Appbar from '../components/Appbar.js';
import styles from '../assets/jss/components/mainmenuStyle.js';
import { theme1 } from '../assets/jss/masterStyle.js';

import routes from '../routes.js';

import themes from '../assets/data/themes.json';
const useStyles = makeStyles(styles);

export default function MainMenu() {
    const classes = useStyles();

    // states 
    const [activeTheme, setTheme] = React.useState(theme)

    const onChange = (event, theme) => {

    }

    // set it to defualt until a file manager is coded
    let theme;
    themes.forEach(th => {
        if (typeof (th) != undefined) {
            if (th.themeName === "default") {
                theme = th;
            }
        }
    })

    const switchRoutes = (
        <Switch>
            {routes.map((prop, index) => {
                return (
                    <Route
                        key={index}
                        path={prop.path}
                        render={(props) => <prop.component {...props} theme={theme} />}
                    >
                    </Route>
                );
            })}
        </Switch>
    );

    return (
        <MuiThemeProvider theme={theme1}>
            <CssBaseline />
            <div className={classes.wrapper}>
                {/* Remove Appbar for now */}
                {/* <Appbar
                    routes={routes}
                /> */}
                <Sidebar
                    routes={routes}
                    theme={activeTheme}
                    handleChange={() => handleChange}
                />
                <div className={classes.contentsWrapper}>
                    {switchRoutes}
                </div>
            </div>
        </MuiThemeProvider>
    );
}
