const goldenratio = 1.61803398875;

const themeStyle = theme => ({
    container: {
        height: "100vw",
        display: "flex",
        margin: "auto",
        justifyContent: "center",
        alignItems: "center",
    },
    square: {
        width: "60%",
        flexWrap: "wrap",
        display: "flex",
        flex: goldenratio,        
        background: "blue",
        '&::before': {
            content: "''",
            display: "block",
            paddingTop: "100%",
        }
    },
    flexcol: {
        width: "60%",
        flexWrap: "wrap",
        display: "flex",
        flex: "1",
        display: "flex",
        flexDirection: "column",
        flexWrap: "wrap",
        background: "red"
    },
    tallRect:{
        width: "60%",
        flexWrap: "wrap",
        display: "flex",
        flex: goldenratio,
        background: "yellow"
    },
    wideRect:{
        width: "60%",
        flexWrap: "wrap",
        display: "flex",
        flex: "1",
        display: "flex",
        background: "aqua"
    },
    grid: {
        paddingBottom: "50%",
        width: "80%",
        height: "0",
        margin: "auto !important",
    },
    pagename: {
        padding: "1.5rem"
    },
    previewpaper: {
        margin: "2rem",
        paddingBottom: "75%",
        width: "75%",
        height: "0"
    },
})

export default themeStyle;
