import {
    drawerWidth,
    appbarHeight,
} from '../masterStyle.js'

const mainMenuStyle = theme => ({
    wrapper: {
        position: "relative",
        top: "0",
        ...theme.mixins.toolbar,
    },
    contentsWrapper: {       
        marginLeft: drawerWidth,

        // margin when appbar is present 
        // marginTop: appbarHeight,
    },
});

export default mainMenuStyle;
