import {
    containerBaseWidth
} from '../../jss/masterStyle.js';

const phi = 1.61803;

const themeStyle = theme => ({
    container: {
        width: "70vw",
        height: "43.2632880099vw",
        display: "grid",
        gridTemplateColumns: "61.8% 9.02 % 5.58 % 23.6 %",
        gridTemplateRows: "61.8 % 9.02 % 5.58 % 23.6 %",
        gridTemplateAreas: ["A B B B", "A E F C", "A E G C", "A D D C"],
    },
    item: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        width: "100%",
        color: "#F2F2F2",
        fontSize: "1.5em",
        boxSizing: "border-box",
        a: {
            gridArea: A,
            backgroundColor: "blue",
        }, 
        b: {
            gridArea: B,
            backgroundColor: "green",
        },
        c: {
            gridArea: C,
            backgroundColor: "yellow",
        },
    },
    grid: {
        paddingBottom: "50%",
        width: "80%",
        height: "0",
        margin: "auto !important",
    },
    pagename: {
        padding: "1.5rem"
    },
    previewpaper: {
        margin: "2rem",
        paddingBottom: "75%",
        width: "75%",
        height: "0"
    },
})

export default themeStyle;
