import {
    drawerWidth,
    appbarHeight,
} from '../masterStyle.js';

const appbarStyle = theme => ({
    appBar: {
        height: "2rem",
        [theme.breakpoints.up('sm')]: {
            width: `calc(100% - ${drawerWidth})`,
            marginLeft: drawerWidth,
            height: appbarHeight,
        }
    },
    appText: {
        marginLeft: "20px",
        marginTop: "20px",
        fontSize: "1rem",
        [theme.breakpoints.up('sm')]: {
        fontSize: "1.5rem",
        }
    },
    iconButton: {
        paddingTop: "2rem",
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none'
        }
    }
});

export default appbarStyle;
