import {
    appbarHeight
} from '../masterStyle.js';

const dashboardStyle = theme => ({
    container: {
        height: "100%",
        marginTop: "2rem",
        [theme.breakpoints.up('sm')]: {
            marginTop: appbarHeight,
        }
    },
    title: {
        padding: "1.5rem"
    },
    section: {
        marginLeft: "1rem",
        marginRight: "1rem",
        [theme.breakpoints.up("sm")]: {
            marginLeft: "auto",
            marginRight: "auto"
        }
    }
});

export default dashboardStyle;
