import React, { useRef, useLayoutEffect } from 'react';

// import recharts
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// import material ui core 
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

import styles from '../../assets/styles/views/gym/gymgraphStyle.js';

const useStyles = makeStyles(styles);

export default function DashGraph(props) {
    const classes = useStyles();

    // states
    const targetRef = useRef();
    const [dimensions, setDimensions] = React.useState({ width: 0, height: 0 });
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };

    useLayoutEffect(() => {
        if (targetRef.current) {
            setDimensions({
                width: targetRef.current.offsetWidth,
                height: targetRef.current.offsetHeight
            });
        }
    }, []);

    const titleCase = (str) => {
        var splitStr = str.toLowerCase().split(' ');
        for (var i = 0; i < splitStr.length; i++) {
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1);
        }
        return splitStr.join(' ');
    }

    const getData = () => {
        if (props.data.length === 0) {
            return ([
                {
                    "date": "2020-01-11",
                    "weight": 0
                },
                {
                    "date": "2020-01-12",
                    "weight": 0
                },
                {
                    "date": "2020-01-13",
                    "weight": 0
                },
            ]);
        } else {
            let tableData = []
            props.data.map((arr) => {
                tableData.push(arr.data)
            })
            return (tableData);
        }
    }

    const getLabel = () => {
        if (props.data.length === 0) {
            return ("No data");
        } else {
            props.data.map((arr) => {
                return (
                    arr.name
                );
            })
        }
    }
    // style

    return (
        <Paper className={classes.paper} ref={targetRef} >
            <ResponsiveContainer width={dimensions.width} height={dimensions.height} className={classes.container}>
                <LineChart data={getData()}
                    margin={{ top: 30, right: 50, left: 20, bottom: 0 }}>
                    <XAxis dataKey="date" />
                    <YAxis dataKey="weight" domain={['auto', 'auto']} />
                    <CartesianGrid strokeDasharray="3 3" />
                    <Tooltip />
                    <Legend height={36} wrapperStyle={{ top: 0, left: 25 }} />
                    <Line name={getLabel()} type="monotone" dataKey="weight" />
                </LineChart>
            </ResponsiveContainer>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper}>
                        <h2 id="transition-modal-title">Transition modal</h2>
                        <p id="transition-modal-description">react-transition-group animates me.</p>
                    </div>
                </Fade>
            </Modal>
        </Paper>
    );
}
