import {
    drawerWidth,
    appbarHeight,
} from '../masterStyle.js';

const appbarStyle = theme => ({
    appBar: {
        width: `calc(100% - ${drawerWidth})`,
        marginLeft: drawerWidth,
        height: appbarHeight,
    },
    appText: {
        marginLeft: "20px",
        marginTop: "20px",
        fontSize: "25px",
    },
    iconButton: {
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none',
          }
    }
});

export default appbarStyle;
