import {
    drawerWidthPC,
    appbarHeight,
} from '../masterStyle.js';

const appbarStyle = theme => ({
    appBar: {
        height: "56px",
        [theme.breakpoints.up('sm')]: {
            width: `calc(100% - ${drawerWidthPC})`,
            marginLeft: drawerWidthPC,
            height: appbarHeight,
        }
    },
    appText: {
        color: primaryColor,
        [theme.breakpoints.up('sm')]: {
        marginLeft: "20px",
        marginTop: "8px",
        }
    },
    iconButton: {
        paddingTop: "0.5rem",
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none',
        }
    }
});

export default appbarStyle;
