import React from 'react';

// import material-ui/cores from MUI 
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import { Hidden, Drawer } from '@material-ui/core';

// import styles from MUI
import { makeStyles } from '@material-ui/core/styles';

// import files 
import styles from '../assets/jss/components/sidebarStyle.js';
import drawerItems from './list/DrawerList.js';

const useStyles = makeStyles(styles);

export default function SideBar(props) {
    const classes = useStyles();
    const drawerstyles = theme => ({
        paper: {
            width: "250px",
            borderRightWidth: "0px",
            right: "0px",
            backgroundSize: "cover",
            boxShadow: "0 10px 30px -12px rgba(0, 0, 0, 0.42), 0 4px 25px 0px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)",
        }
    })
    const drawerclasses = makeStyles(drawerstyles);

    const [mobileOpen, setMobileOpen] = React.useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    }

    return (
        <div className={classes.wrapper}>
            <Hidden smDown implementation="css">
                <Drawer
                    classes={{ paper: drawerclasses.paper }}
                    onClose={handleDrawerToggle}
                    variant="permanent"
                    ModalProps={{
                        keepMounted: true, // Better open performance on mobile.
                    }}
                    PaperProps={{ backgroundColor: 'blue' }}
                >
                    <Typography
                        className={classes.sidebarTitle}
                        variant="h6"
                    >
                        Record Keeper
                </Typography>
                    <Divider variant="middle" />
                    {drawerItems(props.routes)}
                </Drawer>
            </Hidden>
        </div>
    );
}
