import React from 'react';

// import material ui cores
import { makeStyles, withStyles, useTheme } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import AccordionActions from '@material-ui/core/AccordionActions';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';
import useMediaQuery from '@material-ui/core/useMediaQuery';

// import material ui icons 
import ScheduleIcon from '@material-ui/icons/Schedule';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import LibraryMusicIcon from '@material-ui/icons/LibraryMusic';

import MaterialTable from 'material-table';

import workoutRoutine from '../../assets/data/workoutRoutine.json';

import CountDownTimer from '../../components/CountDownTimer.js';
import styles from '../../assets/styles/views/dashboard/dashcontainerStyle.js';

import { icons } from '../../assets/styles/masterStyle.js';

const useStyle = makeStyles(styles);

export default function DashContainer(props) {
    // variables
    const theme = useTheme(styles);
    const matches = useMediaQuery(theme.breakpoints.up('sm'));
    // states 
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel, index) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
        props.handleIndexChange(index);
    }
    React.useEffect(() => {
        if (props.checkedSwitch && expanded !== 'panel1') {
            setExpanded('panel1');
        } else if (!props.checkedSwitch & expanded !== false) {
            setExpanded(false)
            props.handleIndexChange(0)
        }
    }, [props.checkedSwitch])

    // functions 
    const titleCase = (str) => {
        var splitStr = str.toLowerCase().split(' ');
        for (var i = 0; i < splitStr.length; i++) {
            splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1);
        }
        return splitStr.join(' ');
    }

    // styles 
    const classes = useStyle();
    const StyledTableCell = withStyles(theme => ({
        head: {
            backgroundColor: props.theme.colors.secondary,
            color: props.theme.colors.primarytext
        },
        body: {
            fontSize: "0.8rem",
            [theme.breakpoints.up("sm")]: {
                fontSize: "1.2rem",
            }
        },
    }))(TableCell)
    const StyledTableRow = withStyles({
        root: {
            [`&:nth-of-type(${props.selectedCell})`]: {
                backgroundColor: props.theme.colors.tertiary,
            },
        },
    })(TableRow)

    return (
        <div className={classes.container}>
            <div className={classes.toggleText}>
                <Typography color="textPrimary" className={classes.typo} variant="h5">Gym</Typography>
                <FormGroup row>
                    <FormControlLabel
                        className={classes.typo}
                        control={<Switch color="primary" checked={props.state.checkedSwitch} onChange={props.handleSwitchChange} name="checkedSwitch" />} createMuiTheme
                        label={<Typography className={classes.switchTypo} variant="h4">In the gym</Typography>}
                    />
                </FormGroup>
            </div>
            {props.state.checkedSwitch && !matches ? <CountDownTimer /> : null}
            <Accordion
                expanded={expanded === 'panel1'}
                onChange={handleChange('panel1', 1)}
                className={classes.accordion}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel1c-content"
                    id="panel1c-header"
                >
                    <div className={classes.column}>
                        <ScheduleIcon className={classes.icon} />
                        <Typography className={classes.heading}>Next Workout</Typography>
                    </div>
                    <div className={classes.column}>
                        <Typography className={classes.secondaryHeading}>Muscle Group : {workoutRoutine[0].routineName}</Typography>
                    </div>
                </AccordionSummary>
                <AccordionDetails className={classes.details}>
                    <MaterialTable
                        style={{ width: "100%" }}
                        columns={[
                            { title: 'Exercise', field: 'exercise', },
                            { title: 'Sets', field: 'sets' },
                            { title: 'Reps', field: 'reps' },
                            { title: 'Rest', field: 'rest' },
                        ]}
                        data={
                            (workoutRoutine[0].workouts.map((routine, index) => {
                                return {
                                    exercise: routine.workout.name, sets: routine.sets, reps: routine.reps, rest: routine.rest
                                };
                            }))
                        }
                        options={{
                            showTitle: false,
                            search: false
                        }}
                        icons={icons}
                    />
                    {/* <Table className={classes.table} aria-label="customized table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell><Typography variant="body1">Exercise</Typography></StyledTableCell>
                                <StyledTableCell align="right"><Typography variant="body1">Sets</Typography></StyledTableCell>
                                <StyledTableCell align="right"><Typography variant="body1">Reps</Typography></StyledTableCell>
                                <StyledTableCell align="right"><Typography variant="body1">Rest</Typography></StyledTableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {workoutRoutine[0].workouts.map((routine, index) => {
                                return (
                                    <StyledTableRow onClick={() => { props.handleCellChange(index + 1) }} key={index}>
                                        <StyledTableCell className={classes.tableitem} component="th" scope="row">
                                            <Typography variant="body1">{routine.workout.name}</Typography>
                                        </StyledTableCell>
                                        <StyledTableCell className={classes.tableitem} align="right">
                                            <Typography variant="body1">{routine.sets}</Typography>
                                        </StyledTableCell>
                                        <StyledTableCell className={classes.tableitem} align="right">
                                            <Typography variant="body1">{routine.reps}</Typography>
                                        </StyledTableCell>
                                        <StyledTableCell className={classes.tableitem} align="right">
                                            <Typography variant="body1">{routine.rest}</Typography>
                                        </StyledTableCell>
                                    </StyledTableRow>
                                );
                            })}
                        </TableBody>
                    </Table> */}
                </AccordionDetails>
                <Divider />
                <AccordionActions>
                    <Button color="secondary" size="large">More Details</Button>
                </AccordionActions>
            </Accordion>
            <Typography className={classes.typo} variant="h5">Piano</Typography>
            <Accordion expanded={expanded === 'panel3'} onChange={handleChange('panel3', 3)} className={classes.accordion}>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel1c-content"
                    id="panel1c-header"
                >
                    <div className={classes.column}>
                        <LibraryMusicIcon className={classes.icon} />
                        <Typography className={classes.heading}>Pieces</Typography>
                    </div>
                    <div className={classes.column}>
                        <Typography className={classes.secondaryHeading}>In Progress</Typography>
                    </div>
                </AccordionSummary>
                <AccordionDetails >
                        <MaterialTable
                            style={{ width: "100%" }}
                            columns={[
                                { title: 'Piece Name', field: 'name', },
                                { title: 'Composer', field: 'composer' },
                                { title: 'Date Added', field: 'date', type: 'numeric' },
                            ]}
                            data={[
                                { name: 'Waltz in C Sharp Minor (Op. 64 No. 2)', composer: 'Chopin', url: 'https://www.youtube.com/embed/SUT_0c2QVzo' },
                                { name: 'Howls Moving Castle', composer: 'Joe Hisaishi', url: 'https://www.youtube.com/embed/5u5oCjrIu60' },
                                { name: 'Chopin - Nocturne in E Flat Major (Op. 9 No. 2)', composer: 'Chopin', url: 'https://www.youtube.com/embed/p29JUpsOSTE' },
                                { name: 'Joe Hisaishi - One Summers Day', composer: 'Joe Hisaishi', url: 'https://www.youtube.com/embed/TK1Ij_-mank' },
                                { name: 'Kioku', composer: 'Unknown', url: 'https://www.youtube.com/embed/nj93DxZdwUs' }
                            ]}
                            options={{
                                showTitle: false,
                            }}
                            icons={icons}
                            detailPanel={rowData => {
                                return (
                                    <iframe
                                        width="100%"
                                        height="400"
                                        src={rowData.url}
                                        frameBorder="0"
                                        allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen
                                    />
                                )
                            }}
                            onRowClick={(event, rowData, togglePanel) => togglePanel()}
                        />
                </AccordionDetails>
                <Divider />
                <AccordionActions>
                    <Button color="secondary" size="large">More Details</Button>
                </AccordionActions>
            </Accordion>
        </div>
    );
}
